mapper.select:
select

 from product


    and id = #{p.id,jdbcType=BIGINT}


    and id is null


    and name = #{p.name,jdbcType=VARCHAR}


    and name is null


    and price = #{p.price,jdbcType=INTEGER}


    and price is null


    and sku = #{p.prop2u,jdbcType=BIGINT}


    and sku is null


    and "xyz_rank_CLI" = #{p.rank,jdbcType=INTEGER}


    and "xyz_rank_CLI" is null


    and "CLI_first_name_ab" = #{p.first_name_ab,jdbcType=VARCHAR}


    and "CLI_first_name_ab" is null



  order by ${o}

mapper.insert:
