mapper.select:
select

 from types_date_time


    and id = #{p.id,jdbcType=INTEGER}


    and id is null


    and dat1 = #{p.dat1,jdbcType=DATE}


    and dat1 is null


    and ts1 = #{p.ts1,jdbcType=TIMESTAMP}


    and ts1 is null


    and ts2 = #{p.ts2,jdbcType=TIMESTAMP}


    and ts2 is null


    and ts3 = #{p.ts3,jdbcType=TIMESTAMP}


    and ts3 is null


    and ts4 = #{p.ts4,jdbcType=TIMESTAMP}


    and ts4 is null


    and ts5 = #{p.ts5,jdbcType=TIMESTAMP}


    and ts5 is null


    and tim1 = #{p.tim1,jdbcType=TIME}


    and tim1 is null


    and tim2 = #{p.tim2,jdbcType=TIME}


    and tim2 is null


    and tim3 = #{p.tim3,jdbcType=TIME}


    and tim3 is null


    and tim4 = #{p.tim4,jdbcType=TIME}


    and tim4 is null


    and tim5 = #{p.tim5,jdbcType=TIME}


    and tim5 is null



  order by ${o}

mapper.insert:
insert into types_date_time (

    , id
    , dat1
    , ts1
    , ts2
    , ts3
    , ts4
    , ts5
    , tim1
    , tim2
    , tim3
    , tim4
    , tim5

  ) values (

    , #{id,jdbcType=INTEGER}
    , #{dat1,jdbcType=DATE}
    , #{ts1,jdbcType=TIMESTAMP}
    , #{ts2,jdbcType=TIMESTAMP}
    , #{ts3,jdbcType=TIMESTAMP}
    , #{ts4,jdbcType=TIMESTAMP}
    , #{ts5,jdbcType=TIMESTAMP}
    , #{tim1,jdbcType=TIME}
    , #{tim2,jdbcType=TIME}
    , #{tim3,jdbcType=TIME}
    , #{tim4,jdbcType=TIME}
    , #{tim5,jdbcType=TIME}

  )

