    , #{name,jdbcType=VARCHAR}
    , #{propName,jdbcType=VARCHAR}
    , #{referrerId,jdbcType=INTEGER}
    , #{friendId,jdbcType=INTEGER}
    , #{groupAccountId,jdbcType=INTEGER}
    , #{branchId,jdbcType=INTEGER}

  )

