mapper.select:
select

 from employee


    and id = #{p.id,jdbcType=INTEGER}


    and name = #{p.name,jdbcType=VARCHAR}


    and branch_id = #{p.branchId,jdbcType=INTEGER}


    and salary = #{p.salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler}



  order by ${o}

mapper.insert:
insert into employee (

    , id
    , name
    , branch_id
    , salary

  ) values (

    , #{id,jdbcType=INTEGER}
    , #{name,jdbcType=VARCHAR}
    , #{branchId,jdbcType=INTEGER}
    , #{salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler}

  )

mapper.update:
update employee set
  name = #{name,jdbcType=VARCHAR},
  branch_id = #{branchId,jdbcType=INTEGER},
  salary = #{salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler}
 where
  id = #{id,jdbcType=INTEGER}

update employee

  id = #{values.id,jdbcType=INTEGER},
  name = #{values.name,jdbcType=VARCHAR},
  branch_id = #{values.branchId,jdbcType=INTEGER},
  salary = #{values.salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler},



    and id = #{filter.id,jdbcType=INTEGER}


    and name = #{filter.name,jdbcType=VARCHAR}


    and branch_id = #{filter.branchId,jdbcType=INTEGER}


    and salary = #{filter.salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler}

mapper.delete:
delete from employee


    and id = #{id,jdbcType=INTEGER}


    and name = #{name,jdbcType=VARCHAR}


    and branch_id = #{branchId,jdbcType=INTEGER}


    and salary = #{salary,jdbcType=INTEGER,typeHandler=app.daos.primitives.EmployeeDAO$SalaryTypeHandler}
