mapper.select:
select

 from public.vehicle


    and id = #{p.id,jdbcType=INTEGER}


    and name = #{p.name,jdbcType=VARCHAR}


    and mileage = #{p.mileage,jdbcType=INTEGER}


    and version_number = #{p.versionNumber,jdbcType=SMALLINT}


    and vtype = #{p.vtype,jdbcType=INTEGER}



  order by ${o}

mapper.insert:
insert into public.vehicle (

    , id
    , name
    , mileage
    , version_number
    , vtype

  ) values (

    , #{id,jdbcType=INTEGER}
    , #{name,jdbcType=VARCHAR}
    , #{mileage,jdbcType=INTEGER}
    , #{versionNumber,jdbcType=SMALLINT}
    , #{vtype,jdbcType=INTEGER}

  )

mapper.update:
update public.vehicle

  id = #{values.id,jdbcType=INTEGER},
  name = #{values.name,jdbcType=VARCHAR},
  mileage = #{values.mileage,jdbcType=INTEGER},
  version_number = #{values.versionNumber,jdbcType=SMALLINT},
  vtype = #{values.vtype,jdbcType=INTEGER},



    and id = #{filter.id,jdbcType=INTEGER}


    and name = #{filter.name,jdbcType=VARCHAR}


    and mileage = #{filter.mileage,jdbcType=INTEGER}


    and version_number = #{filter.versionNumber,jdbcType=SMALLINT}


    and vtype = #{filter.vtype,jdbcType=INTEGER}

mapper.delete:
delete from public.vehicle


    and id = #{id,jdbcType=INTEGER}


    and name = #{name,jdbcType=VARCHAR}


    and mileage = #{mileage,jdbcType=INTEGER}


    and version_number = #{versionNumber,jdbcType=SMALLINT}


    and vtype = #{vtype,jdbcType=INTEGER}
