mapper.select:
select

 from user1.invoice


    and id = #{p.id,jdbcType=NUMERIC}


    and amount = #{p.amount,jdbcType=NUMERIC}


    and branch_id = #{p.branchId,jdbcType=NUMERIC}


    and account_id = #{p.accountId,jdbcType=NUMERIC}


    and status = #{p.status,jdbcType=VARCHAR}


    and type = #{p.type,jdbcType=VARCHAR}


    and order_date = #{p.orderDate,jdbcType=TIMESTAMP}


    and unpaid_balance = #{p.unpaidBalance,jdbcType=NUMERIC}



  order by ${o}

