    , #{amount,jdbcType=INTEGER}
    , #{branchId,jdbcType=INTEGER}
    , #{accountId,jdbcType=INTEGER}
    , #{unpaidBalance,jdbcType=INTEGER}
    , #{type,jdbcType=VARCHAR}
    , #{status,jdbcType=VARCHAR}
    , #{orderDate,jdbcType=DATE}

  )

