mapper.select:
select

 from numbers


    and "int1" = #{p.int1,jdbcType=SMALLINT}


    and "int2" = #{p.int2,jdbcType=INTEGER}


    and "int3" = #{p.int3,jdbcType=BIGINT}


    and "int_total_amount" = #{p.intTotalAmount,jdbcType=INTEGER}


    and "columns" = #{p.columns,jdbcType=INTEGER}


    and "dec1" = #{p.dec1,jdbcType=NUMERIC}


    and "dec2" = #{p.dec2,jdbcType=NUMERIC}


    and "dec3" = #{p.dec3,jdbcType=NUMERIC}


    and "dec4" = #{p.dec4,jdbcType=NUMERIC}


    and "dec5" = #{p.dec5,jdbcType=NUMERIC}


    and "dec6" = #{p.dec6,jdbcType=NUMERIC}


    and "dec7" = #{p.dec7,jdbcType=NUMERIC}


    and "dec_total_amount" = #{p.decTotalAmount,jdbcType=NUMERIC}


    and "flo1" = #{p.flo1,jdbcType=REAL}


    and "flo2" = #{p.flo2,jdbcType=DOUBLE}


    and "flo_total_amount" = #{p.floTotalAmount,jdbcType=REAL}



  order by ${o}

mapper.update:
update numbers

  "int1" = #{values.int1,jdbcType=SMALLINT},
  "int2" = #{values.int2,jdbcType=INTEGER},
  "int3" = #{values.int3,jdbcType=BIGINT},
  "int_total_amount" = #{values.intTotalAmount,jdbcType=INTEGER},
  "columns" = #{values.columns,jdbcType=INTEGER},
  "dec1" = #{values.dec1,jdbcType=NUMERIC},
  "dec2" = #{values.dec2,jdbcType=NUMERIC},
  "dec3" = #{values.dec3,jdbcType=NUMERIC},
  "dec4" = #{values.dec4,jdbcType=NUMERIC},
  "dec5" = #{values.dec5,jdbcType=NUMERIC},
  "dec6" = #{values.dec6,jdbcType=NUMERIC},
  "dec7" = #{values.dec7,jdbcType=NUMERIC},
  "dec_total_amount" = #{values.decTotalAmount,jdbcType=NUMERIC},
  "flo1" = #{values.flo1,jdbcType=REAL},
  "flo2" = #{values.flo2,jdbcType=DOUBLE},
  "flo_total_amount" = #{values.floTotalAmount,jdbcType=REAL},



    and "int1" = #{filter.int1,jdbcType=SMALLINT}


    and "int2" = #{filter.int2,jdbcType=INTEGER}


    and "int3" = #{filter.int3,jdbcType=BIGINT}


    and "int_total_amount" = #{filter.intTotalAmount,jdbcType=INTEGER}


    and "columns" = #{filter.columns,jdbcType=INTEGER}


    and "dec1" = #{filter.dec1,jdbcType=NUMERIC}


    and "dec2" = #{filter.dec2,jdbcType=NUMERIC}


    and "dec3" = #{filter.dec3,jdbcType=NUMERIC}


    and "dec4" = #{filter.dec4,jdbcType=NUMERIC}


    and "dec5" = #{filter.dec5,jdbcType=NUMERIC}


    and "dec6" = #{filter.dec6,jdbcType=NUMERIC}


    and "dec7" = #{filter.dec7,jdbcType=NUMERIC}


    and "dec_total_amount" = #{filter.decTotalAmount,jdbcType=NUMERIC}


    and "flo1" = #{filter.flo1,jdbcType=REAL}


    and "flo2" = #{filter.flo2,jdbcType=DOUBLE}


    and "flo_total_amount" = #{filter.floTotalAmount,jdbcType=REAL}

