mapper.select:
select

 from other


    and id = #{p.id,jdbcType=INTEGER}


    and boo1 = #{p.boo1,jdbcType=BOOLEAN}


    and boo2 = #{p.boo2,jdbcType=BOOLEAN}


    and boo3 = #{p.boo3,jdbcType=BOOLEAN}


    and oth1 = #{p.oth1,jdbcType=JAVA_OBJECT}


    and idn1 = #{p.idn1,jdbcType=BINARY}


    and geo1 = #{p.geo1,jdbcType=OTHER}



  order by ${o}

mapper.insert:
insert into other (

    , id
    , boo1
    , boo2
    , boo3
    , oth1
    , idn1
    , geo1

  ) values (

    , #{id,jdbcType=INTEGER}
    , #{boo1,jdbcType=BOOLEAN}
    , #{boo2,jdbcType=BOOLEAN}
    , #{boo3,jdbcType=BOOLEAN}
    , #{oth1,jdbcType=JAVA_OBJECT}
    , #{idn1,jdbcType=BINARY}
    , #{geo1,jdbcType=OTHER}

  )

mapper.update:
update other

  id = #{values.id,jdbcType=INTEGER},
  boo1 = #{values.boo1,jdbcType=BOOLEAN},
  boo2 = #{values.boo2,jdbcType=BOOLEAN},
  boo3 = #{values.boo3,jdbcType=BOOLEAN},
  oth1 = #{values.oth1,jdbcType=JAVA_OBJECT},
  idn1 = #{values.idn1,jdbcType=BINARY},
  geo1 = #{values.geo1,jdbcType=OTHER},



    and id = #{filter.id,jdbcType=INTEGER}


    and boo1 = #{filter.boo1,jdbcType=BOOLEAN}


    and boo2 = #{filter.boo2,jdbcType=BOOLEAN}


    and boo3 = #{filter.boo3,jdbcType=BOOLEAN}


    and oth1 = #{filter.oth1,jdbcType=JAVA_OBJECT}


    and idn1 = #{filter.idn1,jdbcType=BINARY}


    and geo1 = #{filter.geo1,jdbcType=OTHER}

